CREATE DATABASE IF NOT EXISTS `jokes_service` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci;
USE `jokes_service`;

CREATE TABLE `joke_categories` (
                                   `id` int NOT NULL,
                                   `name` varchar(255) NOT NULL,
                                   PRIMARY KEY (`id`),
                                   UNIQUE KEY `joke_categories_UNIQUE` (`id`),
                                   UNIQUE KEY `cat_name_UNIQUE` (`name`)
);
CREATE TABLE `jokes`(
                        `id`        int           NOT NULL AUTO_INCREMENT,
                        `guid`      binary(16)    NOT NULL,
                        `setup`     varchar(2048) NOT NULL,
                        `punchline` varchar(2048) NOT NULL,
                        `category`  int           NOT NULL,
                        PRIMARY KEY (`id`),
                        UNIQUE KEY `id_UNIQUE` (`id`),
                        UNIQUE KEY `guid_UNIQUE` (`guid`),
                        KEY `category_idx` (`category`),
                        CONSTRAINT `category` FOREIGN KEY (`category`) REFERENCES `joke_categories` (`id`)
);


